chrome.runtime.onInstalled.addListener(()=>{console.log("Extension Installed"),chrome.storage.local.set({telemetryEnabled:!1})});chrome.runtime.onMessage.addListener((e,l,n)=>{e.type==="toggleTelemetry"&&(chrome.storage.local.set({telemetryEnabled:e.enabled}),n({success:!0}))});chrome.runtime.onMessage.addListener((e,l,n)=>{e.type==="bannerAccepted"?console.log(`[Success] Banner accepted successfully on ${e.details.url}`):e.type==="bannerNotHandled"&&console.warn(`[Failure] Unable to handle banner on ${e.details.url}`),n({received:!0})});
